import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
    const [tasks, setTasks] = useState([]);
    const [newTask, setNewTask] = useState({ title: "", description: "" });
    const navigate = useNavigate();
    const token = localStorage.getItem("token");

    // ✅ Logout
    const handleLogout = () => {
        localStorage.removeItem("token");
        alert("Logged out successfully!");
        navigate("/login");
    };

    // ✅ Fetch All Tasks
    const fetchTasks = async() => {
        try {
            const res = await axios.get("http://localhost:4000/api/v1/tasks", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setTasks(res.data);
        } catch (err) {
            console.error(err);
        }
    };

    // ✅ Fetch Incomplete Tasks
    const fetchIncomplete = async() => {
        try {
            const res = await axios.get("http://localhost:4000/api/v1/tasks/incomplete", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setTasks(res.data);
        } catch (err) {
            console.error(err);
        }
    };

    // ✅ Create Task
    const handleCreate = async(e) => {
        e.preventDefault();
        try {
            await axios.post("http://localhost:4000/api/v1/tasks", newTask, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setNewTask({ title: "", description: "" });
            fetchTasks();
        } catch (err) {
            console.error(err);
        }
    };

    // ✅ Update Task Status
    const handleStatusUpdate = async(id, currentStatus) => {
        const newStatus = currentStatus === "done" ? "todo" : "done";
        try {
            await axios.put(
                `http://localhost:4000/api/v1/tasks/${id}`, { status: newStatus }, { headers: { Authorization: `Bearer ${token}` } }
            );
            fetchTasks();
        } catch (err) {
            console.error(err);
        }
    };

    // ✅ Delete Task
    const handleDelete = async(id) => {
        if (!window.confirm("Are you sure you want to delete this task?")) return;
        try {
            await axios.delete(`http://localhost:4000/api/v1/tasks/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            fetchTasks();
        } catch (err) {
            console.error(err);
        }
    };

    useEffect(() => {
        fetchTasks();
    }, []);

    // ✅ UI Section
    return ( <
        div style = {
            { maxWidth: 600, margin: "50px auto" } } >
        <
        h2 > Dashboard < /h2> <
        button onClick = { handleLogout }
        style = {
            {
                float: "right",
                backgroundColor: "crimson",
                color: "white",
                border: "none",
                padding: "8px 12px",
                borderRadius: "5px",
            }
        } >
        Logout <
        /button>

        <
        h3 > Add Task < /h3> <
        form onSubmit = { handleCreate } >
        <
        input placeholder = "Title"
        value = { newTask.title }
        onChange = {
            (e) =>
            setNewTask({...newTask, title: e.target.value })
        }
        required /
        >
        <
        br / > < br / >
        <
        input placeholder = "Description"
        value = { newTask.description }
        onChange = {
            (e) =>
            setNewTask({...newTask, description: e.target.value })
        }
        required /
        >
        <
        br / > < br / >
        <
        button type = "submit" > Create Task < /button> <
        /form>

        <
        h3 > Your Tasks < /h3>

        <
        button onClick = { fetchIncomplete }
        style = {
            {
                backgroundColor: "goldenrod",
                border: "none",
                padding: "6px 10px",
                borderRadius: "4px",
                marginBottom: "10px",
            }
        } >
        Show Incomplete Tasks <
        /button> <
        button onClick = { fetchTasks }
        style = {
            {
                backgroundColor: "lightgreen",
                border: "none",
                padding: "6px 10px",
                borderRadius: "4px",
                marginLeft: "10px",
            }
        } >
        Show All Tasks <
        /button>

        <
        ul > {
            tasks.map((task) => ( <
                li key = { task.id }
                style = {
                    { marginBottom: "15px" } } >
                <
                b > { task.title } < /b> — <i>{task.status}</i >
                <
                br / > { task.description } <
                br / >
                <
                button onClick = {
                    () => handleStatusUpdate(task.id, task.status) }
                style = {
                    { marginRight: "10px" } } >
                { task.status === "done" ? "Mark Incomplete" : "Mark Done" } <
                /button> <
                button onClick = {
                    () => handleDelete(task.id) }
                style = {
                    { backgroundColor: "tomato", color: "white" } } >
                Delete <
                /button> <
                /li>
            ))
        } <
        /ul> <
        /div>
    );
};

export default Dashboard;
