import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
impo


const Dashboard = () => {
    const [tasks, setTasks] = useState([]);
    const [newTask, setNewTask] = useState({ title: "", description: "" });
    const navigate = useNavigate();
    const token = localStorage.getItem("token");

    useEffect(() => {
        if (!token) {
            navigate("/login");
            return;
        }
        fetchTasks();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [token, navigate]);

    const handleLogout = () => {
        localStorage.removeItem("token");
        alert("Logged out successfully!");
        navigate("/login");
    };

    const fetchTasks = async() => {
        try {
            const res = await axios.get("http://localhost:4001/api/v1/tasks", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setTasks(res.data.tasks || res.data || []);
        } catch (err) {
            console.error(err);
        }
    };

    const fetchIncomplete = async() => {
        try {
            const res = await axios.get("http://localhost:4001/api/v1/tasks/incomplete", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setTasks(res.data.tasks || res.data || []);
        } catch (err) {
            console.error(err);
        }
    };

    const handleCreate = async(e) => {
        e.preventDefault();
        try {
            await axios.post("http://localhost:4001/api/v1/tasks", newTask, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setNewTask({ title: "", description: "" });
            fetchTasks();
        } catch (err) {
            console.error(err);
        }
    };

    const handleStatusUpdate = async(id, currentStatus) => {
        const newStatus = currentStatus === "done" ? "todo" : "done";
        try {
            await axios.put(
                `http://localhost:4001/api/v1/tasks/${id}`, { status: newStatus }, { headers: { Authorization: `Bearer ${token}` } }
            );
            fetchTasks();
        } catch (err) {
            console.error(err);
        }
    };

    const handleDelete = async(id) => {
        if (!window.confirm("Are you sure you want to delete this task?")) return;
        try {
            await axios.delete(`http://localhost:4001/api/v1/tasks/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            fetchTasks();
        } catch (err) {
            console.error(err);
        }
    };

    return ( <
        div style = {
            { maxWidth: 600, margin: "50px auto" }
        } >
        <
        button onClick = { handleLogout }
        style = {
            {
                float: "right",
                backgroundColor: "crimson",
                color: "white",
                border: "none",
                padding: "8px 12px",
                borderRadius: "5px",
            }
        } >
        Logout <
        /button>

        <
        h2 > Dashboard < /h2>

        <
        h3 > Add Task < /h3> <
        form onSubmit = { handleCreate } >
        <
        input placeholder = "Title"
        value = { newTask.title }
        onChange = {
            (e) => setNewTask({...newTask, title: e.target.value })
        }
        required /
        >

        <
        input placeholder = "Description"
        value = { newTask.description }
        onChange = {
            (e) => setNewTask({...newTask, description: e.target.value })
        }
        required /
        >

        <
        button type = "submit" > Create Task < /button> < /
        form >

        <
        h3 > Your Tasks < /h3>

        <
        div >
        <
        button onClick = { fetchIncomplete }
        style = {
            {
                backgroundColor: "goldenrod",
                border: "none",
                padding: "6px 10px",
                borderRadius: "4px",
                marginBottom: "10px",
            }
        } >
        Show Incomplete Tasks <
        /button> <
        button onClick = { fetchTasks }
        style = {
            {
                backgroundColor: "lightgreen",
                border: "none",
                padding: "6px 10px",
                borderRadius: "4px",
                marginLeft: "10px",
            }
        } >
        Show All Tasks <
        /button> < /
        div >

        <
        ul > {
            tasks.map((task) => ( <
                li key = { task._id || task.id }
                style = {
                    { marginBottom: "15px" }
                } >
                <
                b > { task.title } < /b> â€” <i>{task.status}</i >
                <
                div > { task.description } < /div> <
                button onClick = {
                    () => handleStatusUpdate(task._id || task.id, task.status)
                }
                style = {
                    { marginRight: "10px" }
                } > { task.status === "done" ? "Mark Incomplete" : "Mark Done" } <
                /button> <
                button onClick = {
                    () => handleDelete(task._id || task.id)
                }
                style = {
                    { backgroundColor: "tomato", color: "white" }
                } >
                Delete <
                /button> < /
                li >
            ))
        } <
        /ul> < /
        div >
    );
};

export default Dashboard;
