import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Register from "./pages/Register";
import Login from "./pages/Login";
import Dashboard from "./pages/Dashboard";

const App = () => {
    const token = localStorage.getItem("token");

    return (
        <Router>
            <Routes>
                {/* Default route (Home â†’ Login or Dashboard based on token) */}
                <Route
                    path="/"
                    element={token ? <Navigate replace to="/dashboard" /> : <Navigate replace to="/login" />}
                />

                {/* Auth Routes */}
                <Route path="/register" element={<Register />} />
                <Route path="/login" element={<Login />} />

                {/* Protected Dashboard */}
                <Route path="/dashboard" element={token ? <Dashboard /> : <Navigate replace to="/login" />} />

                {/* Catch-all route (404 fallback) */}
                <Route path="*" element={<div style={{ textAlign: "center", marginTop: "100px" }}>404 - Page Not Found</div>} />
            </Routes>
        </Router>
    );
};

export default App;
